import { Component, OnInit, DoCheck, OnDestroy } from '@angular/core';
import { Pelicula } from '../../models/pelicula';

@Component({
  selector: 'app-peliculas',
  templateUrl: './peliculas.component.html',
  styleUrls: ['./peliculas.component.css']
})
export class PeliculasComponent implements OnInit, DoCheck, OnDestroy {

  public titulo: String;
  public peliculas: Pelicula[];
  public favorita: Pelicula;
  
  // Aca se asignan valores a las propiedades de las clases
  // Precargar la cosas
  constructor() { 
    this.titulo = 'Peliculas';
    this.peliculas = [
      new Pelicula('Spiderman 4',2019,'data:image/jpeg;base64,'),
      new Pelicula('Los vengadores Endgame', 2018, 'https://hipertextual.com/files/2019/04/hipertextual-nuevo-trailer-avengers-endgame-agradece-fans-universo-marvel-2019351167.jpg'),
      new Pelicula('Los vengadores Endgame', 2018, 'https://hipertextual.com/files/2019/04/hipertextual-nuevo-trailer-avengers-endgame-agradece-fans-universo-marvel-2019351167.jpg'),
      new Pelicula('Batman vs Superman', 2015, 'data:image/jpeg;base64,')
    ]
  
  }

  // Aca va la logica
  ngOnInit(): void {
    console.log('Componente iniciado')
  }

  // Cada vez que se realiza un cambio se ejecuta el doCheck
  ngDoCheck() {
    console.log('Do check lanzado');
  }

  // Avisa antes de la eliminacion de un componente
  ngOnDestroy() {
    console.log('El componente se va a eliminar');
  }

  cambiarTitulo() {
    this.titulo = 'El titulo ha sido cambiado';
  }

  mostrarFavorita(event, pelicula) {
    this.favorita = event.pelicula;    
  }
}
