import React, { Component } from 'react';
import MiComponente from './MiComponentes';
import Peliculas from './Peliculas';

class SeccionPruebas extends Component {
    
    contador = 0;

    HolaMundo(nombre, edad) {
        var presentacion = (
            <div>
                <h2>Hola, soy {nombre}</h2>
                <h3>Tengo {edad} años</h3>
            </div>
        );

        return presentacion;
    }

    sumar(){
        this.contador++;
    }

    restar(){

    }
    render() {
        var nombre = "Franco Capristo";

        return (
            <section id="content">
                <h2 class="subheader">Últimos artículos</h2>

                <p>
                    Hola Bienvenido al curso de react con Victor Robles WEB
                </p>
                <h2 class="subheader">Funciones y JSX basico</h2>
                {this.HolaMundo(nombre, 24)}

                <h2 class="subheader">Componentes</h2>
                <section className="componentes">
                    <MiComponente />
                    <Peliculas />
                </section>

                <h2 class="subheader">Estados</h2>
                <p>
                    Contador: {this.contador}
                </p>
                <p>
                    <input type="button" value="Sumar" onClick={sumar}/>
                    <input type="bytton" value="Restar" onClick={restar}/>
                </p>
            </section>
        );
    }
}

export default SeccionPruebas;
