import React, {Component} from 'react';

class MiComponente extends Component {

    // Metodo render se encarga de mostrar informacion por pantalla (Mostrar la vista de ese componente)
    render(){
        // El return puede devolver solo 1 etiquete
        // <React.Fragment> salta la anterior restriccion
        let receta = {
            nombre: 'Pizaa',
            ingredientes: ['Tomate', 'Queso',
        'Jamon cocido'],
            calorias: 400
        }
        return(
            <React.Fragment> 
                <h1>Hola, soy el componente llamado: MiComponente</h1>
                <h2>Estoy probando el componente</h2>    
                <hr/>
            </React.Fragment>
            
        ); 
    }

}

export default MiComponente;
