import React, { Component } from 'react';
import axios from 'axios';
import Global from '../Global';

class Articles extends Component {

    url = Global.url;

    state = {
        articles: {},
        statu: null
    };

    componentWillMount() {
        this.getArticles();
    }

    getArticles = () => {
        axios.get(this.url + 'articles')
            .then(res => {
                this.setState({
                    articles: res.data.articles,
                    status: 'success'
                });

            });
    }

    render() {
        if (this.state.articles.length >= 1 && this.state.status === 'success') {

            var listArticles = this.state.articles.map((article) => {
                return (
                    <article className="article-item" id="article-template">
                        <div className="image-wrap">
                            {article.image !== null ?(
                                <img src={this.url+'get-image/'+article.image} alt={article.title} />
                            ) : (
                                <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTS32yfDDOSukhxD-jdQuHV1auz3Vl_eS0p2w&usqp=CAU" alt={article.title} />
                            )
                            }
                        </div>

                        <h2>{article.title}</h2>
                        <span className="date">
                            {article.date}
                        </span>
                        <a href="article.html">Leer más</a>

                        <div className="clearfix"></div>
                    </article>
                );
            });

            return (
                <div id="articles">
                    {listArticles}
                </div>
            );
        } else if (this.state.articles.length === 0) {
            return (
                <div id="articles">
                    <h2 className="subheader">No hay articulos para mostrar</h2>
                    <p>Todavia no hay contenido en esta sección</p>
                </div>
            );
        } else {
            return (
                <div id="articles">
                    <h2 className="subheader">Cargando...</h2>
                    <p>Espere mientras carga el contenido</p>
                </div>
            );
        }

    }
}

export default Articles;
