import React, { Component } from 'react';
import Pelicula from './Pelicula';

class Peliculas extends Component {
    state = {
    };

    cambiarTitulo = () => {

        var { peliculas } = this.state;
        peliculas[0].titulo = "Batman Begins";

        this.setState({
            peliculas: peliculas
        });
    }

    favorita = (pelicula, indice) => {
        this.setState({
            favorita: pelicula
        });
    }

    componentWillMount() {
        //alert("Se va a montar el componente");
        this.setState({
            peliculas: [
                { titulo: 'Batman vs Superman', image: 'data:image/jpeg;base64,' },
                { titulo: 'Gran torino', image: 'data:image/jpeg;base64,' },
                { titulo: 'Looper', image: 'https://pics.filmaffinity.com/Looper-874353819-large.jpg' }
            ],
            nombre: 'Franco Capristo',
            favorita: ''
        })
    }

    componentDidMount() {
        //alert("Ya se ha montado el componente");
    }

    componentWillUnmount() {
        //alert("Me voy a desmontar");
    }

    render() {
        var pStyle = {
            background: 'green',
            color: 'white',
            padding: '10px'
        };

        var favorita;
        if (this.state.favorita.titulo) {
            favorita = (
                <p className="favorita" style={pStyle}>
                    <strong>La pelicula favorita es: </strong>
                    <span>{this.state.favorita.titulo}</span>
                </p>
            );
        } else {
            favorita = (<p>NO HAY PELICULA FAVORITA</p>);
        }

        return (
            <div id="peliculas">
                <Slider
                    /* PROPS */
                    title="Blog"
                    size="slider-small"
                /> 
                <div>
                <div id='content' className="peliculas">
                    <h2 className="subheader">Peliculas</h2>
                    <p>Selección de las peliculas favoritas de {this.state.nombre}</p>

                    <div><button onClick={this.cambiarTitulo}>Cambiar titulo de batman</button></div>

                    {/* CONDICIONAL IF. Verifica si existe el titulo de favorita */
                        /*this.state.favorita.titulo ? (
                            <p className="favorita" style={pStyle}>
                                <strong>La pelicula favorita es: </strong>
                                <span>{this.state.favorita.titulo}</span>
                            </p>
                        ) : (
                            <p>NO HAY PELICULA FAVORITA</p>
                        )
                        */
                        favorita
                    }


                    {/* Crear componente pelicula */}

                    <div id="articles" className="peliculas">
                        {
                            this.state.peliculas.map((pelicula, i) => {
                                return (
                                    <Pelicula
                                        key={i}
                                        pelicula={pelicula}
                                        indice={i}
                                        marcarFavorita={this.favorita}
                                    />
                                );
                            })
                        }
                    </div>
                </div>
                </div>
            </div>
        );
    }

}

export default Peliculas;
